package com.smartkart.model; import jakarta.persistence.*; @Entity public class CartItem { @Id @GeneratedValue(strategy=GenerationType.IDENTITY) private Long id; @ManyToOne(optional=false) private User user; @ManyToOne(optional=false) private Product product; private int quantity = 1; public Long getId(){return id;} public void setId(Long id){this.id=id;} public User getUser(){return user;} public void setUser(User u){this.user=u;} public Product getProduct(){return product;} public void setProduct(Product p){this.product=p;} public int getQuantity(){return quantity;} public void setQuantity(int q){this.quantity=q;} }