package com.smartkart.model; import jakarta.persistence.*; import java.time.LocalDateTime; import java.util.*; @Entity @Table(name="orders") public class Order { @Id @GeneratedValue(strategy=GenerationType.IDENTITY) private Long id; @ManyToOne(optional=false) private User user; private LocalDateTime createdAt = LocalDateTime.now(); @OneToMany(cascade=CascadeType.ALL, orphanRemoval=true) private List<OrderItem> items = new ArrayList<>(); public Long getId(){return id;} public void setId(Long id){this.id=id;} public User getUser(){return user;} public void setUser(User u){this.user=u;} public LocalDateTime getCreatedAt(){return createdAt;} public void setCreatedAt(LocalDateTime t){this.createdAt=t;} public List<OrderItem> getItems(){return items;} public void setItems(List<OrderItem> i){this.items=i;} }