package com.smartkart.model; import jakarta.persistence.*; import java.math.BigDecimal; @Entity public class OrderItem { @Id @GeneratedValue(strategy=GenerationType.IDENTITY) private Long id; @ManyToOne(optional=false) private Product product; private int quantity; private BigDecimal price; public Long getId(){return id;} public void setId(Long id){this.id=id;} public Product getProduct(){return product;} public void setProduct(Product p){this.product=p;} public int getQuantity(){return quantity;} public void setQuantity(int q){this.quantity=q;} public BigDecimal getPrice(){return price;} public void setPrice(BigDecimal pr){this.price=pr;} }