package com.smartkart.model; import jakarta.persistence.*; import jakarta.validation.constraints.*; @Entity @Table(name="users") public class User { @Id @GeneratedValue(strategy=GenerationType.IDENTITY) private Long id; @Email @NotBlank @Column(unique=true, nullable=false) private String email; @NotBlank private String password; @NotBlank private String name; @Enumerated(EnumType.STRING) private Role role = Role.USER; public Long getId(){return id;} public void setId(Long id){this.id=id;} public String getEmail(){return email;} public void setEmail(String e){this.email=e;} public String getPassword(){return password;} public void setPassword(String p){this.password=p;} public String getName(){return name;} public void setName(String n){this.name=n;} public Role getRole(){return role;} public void setRole(Role r){this.role=r;} }